export function createHTMLElementHandle(ref, ...args) {
    return args.reduce((handle, prop)=>{
        if (ref.current?.[prop] !== undefined) {
            const val = ref.current?.[prop];
            handle[prop] = typeof val === 'function' ? (...args)=>val?.apply(ref.current, args) : val;
        }
        return handle;
    }, {});
}
